import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const SignUp = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [role, setRole] = useState("Student"); // Default role
  const navigate = useNavigate();

  const handleSignUp = (e) => {
    e.preventDefault();

    if (password !== confirmPassword) {
      alert("Passwords do not match!");
      return;
    }

    // Simulate successful signup process
    navigate("/signup-success", { state: { email, role } });
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-200 p-4">
      {/* Main Container */}
      <div className="flex bg-white p-8 rounded-lg shadow-md w-full max-w-2xl">
        {/* Left Side - Image */}
        <div className="w-1/2 flex justify-center items-center">
          <img
            src="https://cdn-icons-png.flaticon.com/512/201/201634.png"
            alt="Sign Up"
            className="w-60"
          />
        </div>

        {/* Right Side - Sign-Up Form */}
        <div className="w-1/2">
          <h2 className="text-2xl font-bold text-gray-800 text-center mb-4">
            Create an Account
          </h2>
          <form onSubmit={handleSignUp}>
            <div className="mb-4">
              <input
                type="email"
                placeholder="Enter your email"
                className="w-full px-4 py-2 border rounded-md focus:ring-2 focus:ring-indigo-400"
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
            <div className="mb-4">
              <input
                type="password"
                placeholder="Enter password"
                className="w-full px-4 py-2 border rounded-md focus:ring-2 focus:ring-indigo-400"
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <div className="mb-4">
              <input
                type="password"
                placeholder="Confirm password"
                className="w-full px-4 py-2 border rounded-md focus:ring-2 focus:ring-indigo-400"
                onChange={(e) => setConfirmPassword(e.target.value)}
                required
              />
            </div>

            {/* Role Selection */}
            <div className="mb-4">
              <select
                className="w-full px-4 py-2 border rounded-md focus:ring-2 focus:ring-indigo-400"
                value={role}
                onChange={(e) => setRole(e.target.value)}
              >
                <option value="Student">Student</option>
                <option value="Instructor">Instructor</option>
                <option value="Admin">Admin</option>
              </select>
            </div>

            <button
              type="submit"
              className="w-full bg-indigo-600 text-white py-2 rounded-md hover:bg-indigo-700 transition"
            >
              Sign Up
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default SignUp;
