import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Home from "../components/Home";
import Courses from "../components/Courses";
import Grades from "../components/Grades";
import Reports from "../components/Reports";

const InstructorDashboard = () => {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState("home");

  useEffect(() => {
    if (localStorage.getItem("isLoggedIn") !== "true") {
      navigate("/login");
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("isLoggedIn");
    navigate("/login");
  };

  return (
    <div className="flex min-h-screen bg-gray-100">
      {/* Sidebar */}
      <aside className="w-64 bg-indigo-800 text-white flex flex-col p-5">
        <h2 className="text-xl font-bold mb-6">Skillcode</h2>
        <nav className="space-y-4">
          <button
            className={`flex items-center space-x-2 w-full text-left ${
              activeTab === "home" ? "font-bold" : ""
            }`}
            onClick={() => setActiveTab("home")}
          >
            <span>üè†</span> <span>Home</span>
          </button>
          <button
            className={`flex items-center space-x-2 w-full text-left ${
              activeTab === "courses" ? "font-bold" : ""
            }`}
            onClick={() => setActiveTab("courses")}
          >
            <span>üìö</span> <span>Courses</span>
          </button>
          <button
            className={`flex items-center space-x-2 w-full text-left ${
              activeTab === "grades" ? "font-bold" : ""
            }`}
            onClick={() => setActiveTab("grades")}
          >
            <span>üìù</span> <span>Grades</span>
          </button>
          <button
            className={`flex items-center space-x-2 w-full text-left ${
              activeTab === "reports" ? "font-bold" : ""
            }`}
            onClick={() => setActiveTab("reports")}
          >
            <span>üìä</span> <span>Reports</span>
          </button>
        </nav>
        <button
          onClick={handleLogout}
          className="mt-6 bg-red-500 text-white px-3 py-1 rounded-md hover:bg-red-600"
        >
          Logout
        </button>
      </aside>

      {/* Main Content */}
      <main className="flex-1 p-6">
        {activeTab === "home" && <Home />}
        {activeTab === "courses" && <Courses />}
        {activeTab === "grades" && <Grades />}
        {activeTab === "reports" && <Reports />}
      </main>
    </div>
  );
};

export default InstructorDashboard;
