import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [formData, setFormData] = useState({ email: "", password: "" });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const storedUser = JSON.parse(localStorage.getItem("user"));

    if (
      storedUser &&
      formData.email === storedUser.email &&
      formData.password === storedUser.password
    ) {
      alert("Login successful!");
      navigate("/dashboard"); // Redirect to a dashboard or home page
    } else {
      alert("Invalid email or password!");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-200 p-4">
      <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-md">
        <h2 className="text-2xl font-bold text-gray-800 text-center mb-4">
          Welcome back!
        </h2>

        <form onSubmit={handleSubmit}>
          <div className="mt-4">
            <input
              type="email"
              name="email"
              placeholder="Enter email"
              className="w-full px-4 py-2 border rounded-md focus:ring-2 focus:ring-indigo-400"
              onChange={handleChange}
              required
            />
          </div>

          <div className="mt-4">
            <input
              type="password"
              name="password"
              placeholder="Enter password"
              className="w-full px-4 py-2 border rounded-md focus:ring-2 focus:ring-indigo-400"
              onChange={handleChange}
              required
            />
          </div>

          <button
            type="submit"
            className="w-full bg-indigo-600 text-white py-2 rounded-md mt-6 hover:bg-indigo-700 transition"
          >
            Login
          </button>
        </form>

        <p className="text-center mt-4">
          Don't have an account?{" "}
          <button
            onClick={() => navigate("/signup")}
            className="text-indigo-600 hover:underline"
          >
            Sign up
          </button>
        </p>
      </div>
    </div>
  );
};

export default Login;
