import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const ForgotPassword = () => {
  const [email, setEmail] = useState("");
  const [success, setSuccess] = useState(false);
  const navigate = useNavigate();

  const handleResetPassword = (e) => {
    e.preventDefault();

    // Simulating email check (Replace with backend API later)
    const storedUser = JSON.parse(localStorage.getItem("user"));

    if (!storedUser || storedUser.email !== email) {
      setSuccess(false);
      alert("Email not found! Please enter a registered email.");
      return;
    }

    // Simulating sending a reset email
    setSuccess(true);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-200 p-4">
      <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-md">
        <h2 className="text-2xl font-bold text-gray-800 text-center mb-4">
          Reset Your Password
        </h2>

        {!success ? (
          <>
            <p className="text-center text-gray-600 mb-6">
              Enter your email, and we'll send you a password reset link.
            </p>

            <form onSubmit={handleResetPassword}>
              {/* Email Field */}
              <div className="mb-4">
                <input
                  type="email"
                  name="email"
                  placeholder="Enter your email"
                  className="w-full px-4 py-2 border rounded-md focus:ring-2 focus:ring-indigo-400"
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
              </div>

              {/* Reset Button */}
              <button
                type="submit"
                className="w-full bg-indigo-600 text-white py-2 rounded-md hover:bg-indigo-700 transition"
              >
                Send Reset Link
              </button>
            </form>
          </>
        ) : (
          // ✅ Success Message
          <div className="text-center">
            <p className="text-green-600 font-semibold">
              ✅ Password reset link has been sent to your email.
            </p>
            <button
              onClick={() => navigate("/login")}
              className="text-indigo-600 hover:underline mt-4"
            >
              Return to Login
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default ForgotPassword;
